mapper.insert:
INSERT INTO div (
    div_num,
    div_name
) VALUES (
    #{divNum},
    #{divName}
)

mapper.select:
		select seq, title, reg_id, reg_dt
        from (
          	 	select seq, title, reg_id, reg_dt,
                	   row_number() over(order by seq desc) as rNum
          		from Board
   		 	 ) b
  		  where rNum between #{displayPost} and #{postNum}
       	  order by seq desc

mapper.update:

UPDATE BOARD SET CONTENTS = #{contents},
				 MOD_ID = #{modId},
				 MOD_DT = #{modDt},
				 CHILD_REP = #{childRep}
WHERE REG_ID = #{regId}
