mapper.insert:
INSERT INTO files (
	PRODUCT_NUM,		
    IMG,
    THUNIMG
) VALUES (		
	#{productNum},	
    #{img},
    #{thunImg}
)

INSERT INTO files (
	TITLE_IMG,		
    IMG,
    THUNIMG
) VALUES (		
	title_img.NEXTVAL,	
    #{img},
    #{thunImg}
)

mapper.select:
SELECT THUNIMG
	   FROM FILES
	   WHERE PRODUCT_NUM = #{productNum}
